import time
from datetime import datetime

class User:
    def __init__(self, name, level, address):
        self.name = name
        self.level = level
        self.address = address

class Authorization:
    def __init__(self, user, jwt):
        self.user = user
        self.jwt = jwt

class Request:
    def __init__(self, path, method, auth):
        self.path = path
        self.method = method
        self.auth = auth

class AccessGatewayFilter:
    def __init__(self):
        pass

    def filter(self, request):
        request_uri = request.path
        method = request.method

        if self.is_start_with(request_uri):
            return True

        try:
            token = self.get_jwt_user(request)
            user = token.user
            if user.level > 2:
                self.set_current_user_info_and_log(user)
                return True
        except Exception as e:
            print(f"Exception occurred: {e}")
            return False
        return False

    def is_start_with(self, request_uri):
        start_with = ["/api", "/login"]
        for s in start_with:
            if request_uri.startswith(s):
                return True
        return False

    def get_jwt_user(self, request):
        token = request.auth
        user = token.user

        if token.jwt.startswith(user.name):
            jwt_str_date = token.jwt[len(user.name):]

            try:
                jwt_timestamp = float(jwt_str_date)
            except ValueError:
                return Authorization(User("", 0, ""), "")

            now = time.time()
            if (now - jwt_timestamp) >= 72 * 60 * 60:
                return Authorization(User("", 0, ""), "")
        return token

    def set_current_user_info_and_log(self, user):
        current_time = int(time.time())
        print(f"{user.name} {user.address} {current_time}")

# Example usage:
if __name__ == "__main__":
    user = User("JohnDoe", 3, "123 Main St")
    auth = Authorization(user, "JohnDoe1633072800")  # Example JWT with a valid timestamp
    request = Request("/some/path", "GET", auth)

    filter_instance = AccessGatewayFilter()
    result = filter_instance.filter(request)
    print(f"Request filtered: {result}")
