#include <cassert>
#include <ctime>
#include <string>

void test_start_with_api() {
    Request req{"/api/v1/resource", "GET", Authorization{User{"",0,""}, ""}};
    AccessGatewayFilter f;
    assert(f.filter(req) == true);
}

void test_start_with_login() {
    Request req{"/login", "POST", Authorization{User{"",0,""}, ""}};
    AccessGatewayFilter f;
    assert(f.filter(req) == true);
}

void test_valid_high_level_not_expired() {
    std::time_t now = std::time(nullptr);
    User u{"Alice", 3, "1 Road"};
    std::string jwt = u.name + std::to_string(now);
    Authorization auth{u, jwt};
    Request req{"/resource", "GET", auth};
    AccessGatewayFilter f;
    assert(f.filter(req) == true);
}

void test_low_level_denied() {
    std::time_t now = std::time(nullptr);
    User u{"Bob", 2, "2 Road"};
    std::string jwt = u.name + std::to_string(now);
    Authorization auth{u, jwt};
    Request req{"/resource", "GET", auth};
    AccessGatewayFilter f;
    assert(f.filter(req) == false);
}

void test_expired_jwt_denied() {
    std::time_t old = std::time(nullptr) - 4 * 24 * 60 * 60;
    User u{"Carol", 5, "3 Road"};
    std::string jwt = u.name + std::to_string(old);
    Authorization auth{u, jwt};
    Request req{"/resource", "GET", auth};
    AccessGatewayFilter f;
    assert(f.filter(req) == false);
}

void test_malformed_jwt_denied() {
    User u{"Dave", 5, "4 Road"};
    std::string jwt = u.name + std::string("notanumber");
    Authorization auth{u, jwt};
    Request req{"/resource", "GET", auth};
    AccessGatewayFilter f;
    assert(f.filter(req) == false);
}

void test_non_matching_path_denied() {
    User u{"Eve", 1, "5 Road"};
    std::time_t now = std::time(nullptr);
    std::string jwt = u.name + std::to_string(now);
    Authorization auth{u, jwt};
    Request req{"/something/api", "GET", auth};
    AccessGatewayFilter f;
    // path does not start with /api or /login and user level too low
    assert(f.filter(req) == false);
}

void test_apix_matches() {
    Request req{"/apix/whatever", "GET", Authorization{User{"",0,""}, ""}};
    AccessGatewayFilter f;
    // starts with /api so should match
    assert(f.filter(req) == true);
}
