    if (ArrangementCalculator::factorial(1) != 1) throw "test_factorial failed: factorial(1)";
    if (ArrangementCalculator::factorial(5) != 120) throw "test_factorial failed: factorial(5)";
}

void test_count_default() {
    if (ArrangementCalculator::count(5) != 120) throw "test_count_default failed";
}

void test_count_partial() {
    if (ArrangementCalculator::count(5, 3) != 60) throw "test_count_partial failed";
}

void test_count_zero_m() {
    if (ArrangementCalculator::count(5, 0) != 1) throw "test_count_zero_m failed";
}

void test_count_all() {
    if (ArrangementCalculator::count_all(3) != 15) throw "test_count_all failed";
}

void test_select_full() {
    ArrangementCalculator ac(std::vector<std::string>{"a","b","c"});
    auto res = ac.select();
    if (res.size() != 6) throw "test_select_full failed: size";
    if (res.front() != std::vector<std::string>{"a","b","c"}) throw "test_select_full failed: first";
    if (res.back() != std::vector<std::string>{"c","b","a"}) throw "test_select_full failed: last";
}

void test_select_partial_m2() {
    ArrangementCalculator ac(std::vector<std::string>{"x","y","z"});
    auto res = ac.select(2);
    if (res.size() != 6) throw "test_select_partial_m2 failed: size";
    bool found = false;
    for (const auto &v : res) {
        if (v == std::vector<std::string>{"y","z"}) { found = true; break; }
    }
    if (!found) throw "test_select_partial_m2 failed: missing [y,z]";
}

void test_select_all() {
    ArrangementCalculator ac(std::vector<std::string>{"1","2"});
    auto res = ac.select_all();
    if (res.size() != 4) throw "test_select_all failed: size";
    bool found1 = false, found2 = false;
    for (const auto &v : res) {
        if (v == std::vector<std::string>{"2"}) found1 = true;
        if (v == std::vector<std::string>{"2","1"}) found2 = true;
    }
    if (!found1 || !found2) throw "test_select_all failed: missing elements";
}

void test_select_m_gt_n() {
    ArrangementCalculator ac(std::vector<std::string>{"a","b"});
    auto res = ac.select(3);
    if (res.size() != 0) throw "test_select_m_gt_n failed";
}
