void test_add_and_get_gpa_simple() {
    AssessmentSystem sys;
    sys.add_student("Alice", 10, "CS");
    sys.add_course_score("Alice", "Math", 80);
    auto gpa = sys.get_gpa("Alice");
    assert(gpa && (*gpa) == 80.0);
}

void test_multiple_courses_gpa() {
    AssessmentSystem sys;
    sys.add_student("Bob", 11, "EE");
    sys.add_course_score("Bob", "Math", 90);
    sys.add_course_score("Bob", "Physics", 80);
    auto gpa = sys.get_gpa("Bob");
    assert(gpa && ((*gpa) - 85.0 < 1e-9 && 85.0 - (*gpa) < 1e-9));
}

void test_get_all_students_with_fail_course() {
    AssessmentSystem sys;
    sys.add_student("Cathy", 12, "ME");
    sys.add_student("Dan", 12, "CE");
    sys.add_course_score("Cathy", "Chem", 59);
    sys.add_course_score("Dan", "Chem", 75);
    auto fails = sys.get_all_students_with_fail_course();
    bool foundCathy = false;
    for (auto &n : fails) if (n == "Cathy") foundCathy = true;
    assert(foundCathy);
}

