    AssessmentSystem sys;
    sys.add_student("Eve", 10, "CS");
    sys.add_student("Frank", 10, "CS");
    sys.add_course_score("Eve", "Bio", 70);
    sys.add_course_score("Frank", "Bio", 90);
    auto avg = sys.get_course_average("Bio");
    assert(avg && ((*avg) - 80.0 < 1e-9 && 80.0 - (*avg) < 1e-9));
}

void test_get_top_student() {
    AssessmentSystem sys;
    sys.add_student("Grace", 11, "CS");
    sys.add_student("Heidi", 11, "CS");
    sys.add_course_score("Grace", "Math", 95);
    sys.add_course_score("Heidi", "Math", 85);
    auto top = sys.get_top_student();
    assert(top && *top == "Grace");
}

void test_add_existing_student_behavior() {
    AssessmentSystem sys;
    sys.add_student("Ian", 10, "EE");
    // C++ implementation overwrites existing student silently
    sys.add_student("Ian", 11, "ME");
    auto it = sys.students.find("Ian");
    assert(it != sys.students.end());
    assert(it->second.grade == 11);
}

void test_add_course_to_nonexistent_student() {
