void test_top_student_tie() {
    AssessmentSystem sys;
    // Insert Alice then Bob; map iteration is sorted by key, but both names are alphabetical
    sys.add_student("Alice", 12, "CS");
    sys.add_student("Bob", 12, "CS");
    sys.add_course_score("Alice", "Hist", 80);
    sys.add_course_score("Bob", "Hist", 80);
    auto top = sys.get_top_student();
    // Expect one of them; we assert Alice exists as top when tie (alphabetical)
    assert(top && *top == "Alice");
}
