    int w = p.write_json(j, fname);
    if (w != 1) { std::remove(fname); throw "test_write_and_read_cycle: write failed"; }

    nlohmann::json rj;
    int r = p.read_json(fname, rj);
    if (r != 1) { std::remove(fname); throw "test_write_and_read_cycle: read failed"; }
    if (!rj.contains("k") || rj["k"] != 3) { std::remove(fname); throw "test_write_and_read_cycle: content mismatch"; }

    std::remove(fname);
}

void test_process_remove_key_existing() {
    const char* fname = "test_proc1.json";
    std::ofstream ofs(fname);
    ofs << "{ \"rm\": 10, \"keep\": 5 }";
    ofs.close();

    JSONProcessor p;
    int r = p.process_json(fname, "rm");
    if (r != 1) { std::remove(fname); throw "test_process_remove_key_existing: process failed"; }

    nlohmann::json out;
    p.read_json(fname, out);
    if (out.contains("rm")) { std::remove(fname); throw "test_process_remove_key_existing: key still present"; }

    std::remove(fname);
}

