    nlohmann::json out;
    p.read_json(fname, out);
    if (!out.contains("a") || out["a"] != 1) { std::remove(fname); throw "test_process_remove_key_missing: file changed"; }

    std::remove(fname);
}

void test_process_after_removal_persists() {
    const char* fname = "test_proc3.json";
