bool test_all_correct(){
    MetricsCalculator mc;
    std::vector<int> pred = {1,0,1,0};
    std::vector<int> truth = {1,0,1,0};
    double p = mc.precision(pred, truth);
    double r = mc.recall(pred, truth);
    double f1 = mc.f1_score(pred, truth);
    double acc = mc.accuracy(pred, truth);
    double eps = 1e-6;
    return (std::fabs(p - 1.0) < eps) && (std::fabs(r - 1.0) < eps) && (std::fabs(f1 - 1.0) < eps) && (std::fabs(acc - 1.0) < eps);
