}

bool test_add_white_list_duplicate() {
    Server s;
    s.add_white_list(10);
    auto r = s.add_white_list(10);
    if (r.empty()) return true;
    throw std::runtime_error("add_white_list did not return empty on duplicate");
}

bool test_del_white_list_existing() {
    Server s;
    s.add_white_list(20);
    auto r = s.del_white_list(20);
    if (r.empty()) return true;
    throw std::runtime_error("del_white_list did not remove existing addr");
}

bool test_del_white_list_non_existing() {
    Server s;
    auto r = s.del_white_list(99);
    if (r.empty()) return true;
    throw std::runtime_error("del_white_list did not return empty for non-existing addr");
}

bool test_recv_missing_keys() {
    Server s;
    std::map<std::string,std::string> info;
    info["addr"] = "1"; // missing content
    int res = s.recv(info);
    if (res == -1 && s.show("receive").empty()) return true;
    throw std::runtime_error("recv did not handle missing keys correctly");
}

bool test_recv_not_whitelisted() {
    Server s;
    std::map<std::string,std::string> info{{"addr","2"},{"content","x"}};
    int res = s.recv(info);
    if (res == 0 && s.show("receive").empty()) return true;
    throw std::runtime_error("recv allowed non-whitelisted addr");
}

bool test_recv_whitelisted() {
    Server s;
    s.add_white_list(3);
    std::map<std::string,std::string> info{{"addr","3"},{"content","hello"}};
    int res = s.recv(info);
    auto rec = s.show("receive");
    if (res == 1 && rec["addr"] == "3" && rec["content"] == "hello") return true;
    throw std::runtime_error("recv did not record receive_struct correctly");
}

bool test_send_missing_keys() {
    Server s;
    std::map<std::string,std::string> info{{"addr","1"}}; // missing content
    std::string res = s.send(info);
    if (res == "info structure is not correct" ) return true;
    throw std::runtime_error("send did not detect missing keys");
}

bool test_send_and_show() {
    Server s;
    std::map<std::string,std::string> info{{"addr","2"},{"content","bye"}};
    std::string res = s.send(info);
    auto sh = s.show("send");
    if (res == "" && sh["addr"] == "2" && sh["content"] == "bye") return true;
    throw std::runtime_error("send or show did not behave correctly");
}
