void test_add_single_item() {
    ShoppingCart cart;
    cart.add_item("apple", 1.0, 2);
    auto items = cart.view_items();
    assert(items.find("apple") != items.end());
    assert(items["apple"].first == 1.0);
    assert(items["apple"].second == 2);
    assert(fabs(cart.total_price() - 2.0) < 1e-9);
}

void test_add_same_item_accumulate() {
    ShoppingCart cart;
    cart.add_item("banana", 0.5, 1);
    cart.add_item("banana", 0.6, 2);
    auto items = cart.view_items();
    // Expectation (from correct behavior): price updated to 0.6 and quantity accumulated to 3
    assert(items.find("banana") != items.end());
    assert(items["banana"].first == 0.6);
    assert(items["banana"].second == 3);
}

