}

void test_median_even_odd() {
    std::vector<int> odd = {3,1,2};
    std::vector<int> even = {1,4,2,3};
    double mo = Statistics3::median(odd);
    double me = Statistics3::median(even);
    assert(std::fabs(mo - 2.0) < 1e-9);
    assert(std::fabs(me - 2.5) < 1e-9);
}

void test_mode_multiple() {
    std::vector<int> v = {1,2,2,3,3};
    std::vector<int> modes = Statistics3::mode(v);
    std::vector<int> expected = {2,3};
    assert(modes == expected);
}

void test_correlation_perfect() {
    std::vector<int> x = {1,2,3};
    std::vector<int> y = {2,4,6};
    double c = Statistics3::correlation(x,y);
    assert(std::fabs(c - 1.0) < 1e-9);
}

void test_correlation_constant() {
    std::vector<int> x = {1,1,1};
    std::vector<int> y = {2,3,4};
    double c = Statistics3::correlation(x,y);
    assert(std::isnan(c));
}

void test_correlation_mismatch_exception() {
    std::vector<int> x = {1,2};
    std::vector<int> y = {1};
    try {
        double c = Statistics3::correlation(x,y);
        (void)c;
        assert(false);
    } catch (const std::invalid_argument&) {
        // expected
    } catch (...) {
        assert(false);
    }
}

void test_correlation_matrix() {
    std::vector<std::vector<int>> data = {{1,2},{2,4},{3,6}}; // columns perfectly correlated
    auto mat = Statistics3::correlation_matrix(data);
    assert(mat.size() == 2);
    assert(std::fabs(mat[0][0] - 1.0) < 1e-9);
    assert(std::fabs(mat[0][1] - 1.0) < 1e-9);
    assert(std::fabs(mat[1][0] - 1.0) < 1e-9);
    assert(std::fabs(mat[1][1] - 1.0) < 1e-9);
}

void test_standard_deviation() {
    std::vector<int> v = {1,2,3,4};
    double sd = Statistics3::standard_deviation(v);
    double expected = std::sqrt(1.6666666666666667); // sample stddev
    assert(std::fabs(sd - expected) < 1e-9);
}

void test_z_score_normal() {
    std::vector<int> v = {1,2,3};
    auto zs = Statistics3::z_score(v);
    assert(zs.size() == 3);
