    // C++ constructor does not auto-check, so mode should remain "heat".
    return t.get_mode() == "heat";
}

bool test_set_mode_valid() {
    Thermostat t(20, 25, std::string("cool"));
    bool res = t.set_mode("heat");
    return res && t.get_mode() == "heat";
}

