    Thermostat t(18, 22, std::string("cool"));
    t.auto_set_mode();
    return t.get_mode() == "heat";
}

bool test_auto_check_conflict_equal() {
    Thermostat t(20, 20, std::string("cool"));
    // For equal temperatures, C++ auto_check_conflict will auto_set_mode() to "cool" and return false
    bool res = t.auto_check_conflict();
    return res == false && t.get_mode() == "cool";
}

