}

bool test_simulate_cooling() {
    Thermostat t(30, 25, std::string("heat"));
    int time = t.simulate_operation();
    // Should take 5 units to cool from 30 to 25
    return time == 5 && t.current_temperature == 25;
}

bool test_set_target_triggers_autocheck() {
