    auto res = ws.query(m, "celsius");
    double expected = 25.0;
    if (!(res.first > expected - 1e-9 && res.first < expected + 1e-9)) throw "test_query_existing_conversion_to_celsius: temperature mismatch";
    if (res.second != "Clear") throw "test_query_existing_conversion_to_celsius: weather mismatch";
}

