    double expected = 77.0;
    if (!(res.first > expected - 1e-9 && res.first < expected + 1e-9)) throw "test_query_existing_conversion_to_fahrenheit: temperature mismatch";
    if (res.second != "Sunny") throw "test_query_existing_conversion_to_fahrenheit: weather mismatch";
}

