    ws.set_temperature(c);
    double f = ws.celsius_to_fahrenheit();
    double expected_f = 212.0;
    if (!(f > expected_f - 1e-9 && f < expected_f + 1e-9)) throw "test_round_trip_conversion_accuracy: celsius_to_fahrenheit mismatch";
}
